import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import React from 'react';
import Nav from 'react-bootstrap/Nav';
import { Component } from "react";
import LoginForm from './LoginForm';
import SignupForm from './SignupForm';

class LpNav extends Component{
    constructor() {
        super();
        this.state = {
          name: "React",
          showHideLog: false,
          showHideSign: true
        };
        this.hideComponent = this.hideComponent.bind(this);
      }
      hideComponent(name) {
        console.log(name);
        switch (name) {
            case "showHideLog":
               
                this.setState({ showHideLog:true });
                break;
              case "showHideSign":
                this.setState({ showHideSign: true });
                break;
            default:
              this.setState({ showHideLog: !this.state.showHideLog });
        }
      }
    render(){
        const { showHideLog, showHideSign } = this.state;
        return(
    //        <Nav variant="tabs" defaultActiveKey="">
    //   <Nav.Item>
    //     <Nav.Link onClick={() => this.hideComponent("showHideLog")}>Log In</Nav.Link>
    //   </Nav.Item>
    //   <Nav.Item>
    //     <Nav.Link onClick={() => this.hideComponent("showHideSign")}>Sign Up</Nav.Link>
    //   </Nav.Item>
    // </Nav>
    <div>
        {showHideLog && <LoginForm />}
        <hr/>
         {showHideSign && <SignupForm />}
        <hr />
        <div>
          <button onClick={() => this.hideComponent("hideShowLog")}>
            Click here to hide GFG child1 component
          </button>
          <button onClick={() => this.hideComponent("hideShowSign")}>
            Click here to hide GFG child2 component
          </button>
        </div>
      </div>
        );
    }
}
export default LpNav;
