import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
//import React from 'react';
import Nav from 'react-bootstrap/Nav';
//import { Component } from "react";
import LoginForm from './LoginForm';
import SignupForm from './SignupForm';

import React, { Component } from "react";
import { render } from "react-dom";



class LpNav extends Component {
constructor() {
	super();
	this.state = {
	login: true,
	signup: false
	};
	this.hideComponent = this.hideComponent.bind(this);
}

hideComponent(varname) {
	console.log(varname);
	switch (varname) {
	case "login":
		this.setState({ login: !this.state.login });
		this.setState({ signup: !this.state.signup });
		break;
	case "signup":
		this.setState({ signup: !this.state.signup });
		this.setState({ login: !this.state.login });
		break;
	default: return;
	}
}

render() {
	const { login, signup} = this.state;
	return (
	<div>
		{login && <LoginForm />}
		
		{signup && <SignupForm />}
		
		<div>
		<button onClick={() => this.hideComponent("login")}>
			Click here to hide GFG child1 component
		</button>
		<button onClick={() => this.hideComponent("signup")}>
			Click here to hide GFG child2 component
		</button>
		</div>
	</div>
	);
}
}

export default LpNav;
